type Consensus struct {
	NumberOfPBFTMessage int
}

func NewConsensus() *Consensus {
	return &Consensus{
		NumberOfPBFTMessage: 0,
	}
}
func (consensus *Consensus) OnBFTMsg(*wire.MessageBFT) {
	consensus.NumberOfPBFTMessage += 1
}

