	var tx transaction.Tx
	err = json.Unmarshal(rawTxBytes, &tx)
	msg := &wire.MessageTx{Transaction: &tx}
	netSync.Start()
	netSync.cMessage <- msg
	<-time.Tick(1 * time.Second)
